import argparse
from typing import List

import pandas as pd

from pandas.core.series import Series

TITLE_COLUMN_NAME = "title"
CODE_COLUMN_NAME = "code"
HUGGINGFACE_COLUMN_NAME = "huggingface"
COLAB_COLUMN_NAME = "colab"
AUTHORS_COLUMN_NAME = "authors"

AUTOGENERATED_EXPERIMENTS_LIST_TOKEN = "<!--- AUTOGENERATED_EXPERIMENTS_LIST -->"

WARNING_HEADER = [
    "<!---",
    "   WARNING: DO NOT EDIT THIS LIST MANUALLY. IT IS AUTOMATICALLY GENERATED.",
    "   HEAD OVER TO CONTRIBUTING.MD FOR MORE DETAILS ON HOW TO MAKE CHANGES PROPERLY.",
    "-->"
]

GITHUB_BADGE_PATTERN = "[![GitHub](https://badges.aleen42.com/src/github.svg)]({})"
HUGGINGFACE_BADGE_PATTERN = "[![Gradio](https://img.shields.io/badge/%F0%9F%A4%97%20Hugging%20Face-Spaces-blue)]({})"
COLAB_BADGE_PATTERN = "[![Colab](https://colab.research.google.com/assets/colab-badge.svg)]({})"


TABLE_HEADER = [
    "| **experiment** | **complementary materials** | **authors** |",
    "|:--------------:|:---------------------------:|:-----------:|"
]


def read_lines_from_file(path: str) -> List[str]:
    """
    Reads lines from file and strips trailing whitespaces.
    """
    with open(path) as file:
        return [line.rstrip() for line in file]


def save_lines_to_file(path: str, lines: List[str]) -> None:
    """
    Saves lines to file.
    """
    with open(path, "w") as f:
        for line in lines:
            f.write("%s\n" % line)


def format_entry(entry: Series) -> str:
    title = entry.loc[TITLE_COLUMN_NAME]
    code_url = entry.loc[CODE_COLUMN_NAME]
    huggingface_url = entry.loc[HUGGINGFACE_COLUMN_NAME]
    colab_url = entry.loc[COLAB_COLUMN_NAME]
    authors = entry.loc[AUTHORS_COLUMN_NAME]
    code_badge = GITHUB_BADGE_PATTERN.format(
        code_url) if code_url else ""
    huggingface_badge = HUGGINGFACE_BADGE_PATTERN.format(
        huggingface_url) if huggingface_url else ""
    colab_badge = COLAB_BADGE_PATTERN.format(
        colab_url) if colab_url else ""
    complementary_materials = " ".join([code_badge, huggingface_badge, colab_badge])
    return "| {} | {} | {} |".format(title, complementary_materials, authors)


def load_table_entries(path: str) -> List[str]:
    """
    Loads table entries from csv file.
    """
    df = pd.read_csv(path, quotechar='"', dtype=str)
    df.columns = df.columns.str.strip()
    df = df.fillna("")
    return [
        format_entry(row)
        for _, row
        in df.iterrows()
    ]


def search_lines_with_token(lines: List[str], token: str) -> List[int]:
    result = []
    for line_index, line in enumerate(lines):
        if token in line:
            result.append(line_index)
    return result


def inject_markdown_table_into_readme(
        readme_lines: List[str],
        table_lines: List[str]
) -> List[str]:
    lines_with_token_indexes = search_lines_with_token(
        lines=readme_lines,
        token=AUTOGENERATED_EXPERIMENTS_LIST_TOKEN)
    if len(lines_with_token_indexes) != 2:
        raise Exception(f"Please inject two {AUTOGENERATED_EXPERIMENTS_LIST_TOKEN} "
                        f"tokens to signal start and end of autogenerated table.")

    [table_start_line_index, table_end_line_index] = lines_with_token_indexes
    return (
        readme_lines[:table_start_line_index + 1] +
        table_lines +
        readme_lines[table_end_line_index:]
    )


if __name__ == "__main__":
    parser = argparse.ArgumentParser()
    parser.add_argument('-d', '--data_path', default='automation/data.csv')
    parser.add_argument('-r', '--readme_path', default='README.md')
    args = parser.parse_args()

    table_lines = load_table_entries(path=args.data_path)
    table_lines = WARNING_HEADER + TABLE_HEADER + table_lines
    readme_lines = read_lines_from_file(path=args.readme_path)
    readme_lines = inject_markdown_table_into_readme(
        readme_lines=readme_lines,
        table_lines=table_lines)
    save_lines_to_file(path=args.readme_path, lines=readme_lines)
